// --Atmega328P
#define DATA_DDR     DDRD
#define DATA_PORT    PORTD
#define DATA_PIN     PIND

#define COMMAND_DDR_1  DDRC
#define COMMAND_PORT_1 PORTC
#define RST 0
#define RS  1
#define WR  2
#define RD  3

#define COMMAND_DDR_2  DDRB
#define COMMAND_PORT_2 PORTB
#define CS  0

#define swap(a, b) { int16_t t = a; a = b; b = t; }

#define RST_IDLE   COMMAND_PORT_1 |= (1 << RST)
#define WR_IDLE    COMMAND_PORT_1 |= (1 << WR)
#define RD_IDLE    COMMAND_PORT_1 |= (1 << RD)
#define CS_IDLE    COMMAND_PORT_2 |= (1 << CS)
#define RST_ACTIVE COMMAND_PORT_1 &= ~(1 << RST)
#define WR_ACTIVE  COMMAND_PORT_1 &= ~(1 << WR)
#define RD_ACTIVE  COMMAND_PORT_1 &= ~(1 << RD)
#define CS_ACTIVE  COMMAND_PORT_2 &= ~(1 << CS)
#define CD_COMM    COMMAND_PORT_1 &= ~(1 << RS)
#define CD_DATA    COMMAND_PORT_1 |= (1 << RS)

#define setDataPortReadMode() DATA_DDR = 0x00
#define setDataPortWriteMode() DATA_DDR = 0xFF

#define WR_STROBE { WR_ACTIVE; WR_IDLE; }

